# x = "()()))"

defmodule Santa do
  def accumulate(el, acc) do
    case el do
      "(" -> acc + 1
      ")" -> acc - 1
      _ -> acc
    end
  end
end

floor = String.graphemes(x) |> Enum.reduce(0, &Santa.accumulate/2)
m = String.graphemes(x) |> Enum.scan(0, &Santa.accumulate/2) |> Enum.find_index(fn x -> x == -1 end)

IO.puts "After getting instructions, Santa is now located at #{floor} floor"
IO.puts "From all #{String.length(x)} instructions, first which gets him to go to basement was: #{m + 1}!"
